  --shadow-2xl: 0 25px 50px -12px rgb(0 0 0 / 0.5);
}

@layer base {
  * {
    @apply border-border;
  }

  html {
    scroll-behavior: smooth;
    scroll-padding-top: 5rem;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground transition-colors duration-300;
    font-feature-settings: "cv03", "cv04", "cv11";
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }

  h1, h2, h3, h4, h5, h6 {
    @apply font-semibold tracking-tight;
    font-family: var(--font-sans);
  }

  .container {
    @apply mx-auto px-4 sm:px-6 lg:px-8;
    max-width: 1280px;
  }
}

@layer utilities {
  /* Text Gradients */
  .gradient-text {
    @apply bg-gradient-to-r from-primary via-blue-600 to-purple-600 bg-clip-text text-transparent;
    background-size: 200% 200%;
    animation: gradientShift 3s ease-in-out infinite;
  }

  .gradient-text-simple {
    @apply bg-gradient-to-r from-primary to-blue-600 bg-clip-text text-transparent;
  }

  /* Background Gradients */
  .hero-gradient {
    background: linear-gradient(135deg, 
      hsl(var(--background)) 0%, 
      hsl(var(--primary) / 0.05) 50%, 
      hsl(var(--primary) / 0.1) 100%);
  }

  .hero-gradient.dark {
    background: linear-gradient(135deg, 
      hsl(var(--background)) 0%, 
      hsl(var(--primary) / 0.1) 50%, 
      hsl(var(--primary) / 0.2) 100%);
  }

  .section-gradient {
    background: linear-gradient(to bottom, 
      transparent 0%, 
      hsl(var(--accent) / 0.3) 50%, 
      transparent 100%);
  }

  /* Glass Effects */
  .glass-card {
    @apply bg-white/10 backdrop-blur-xl border border-white/20;
  }

  .glass-card.dark {
    @apply bg-black/20 backdrop-blur-xl border border-white/10;
  }

  /* Modern Card Styles */
  .modern-card {
    @apply bg-card border border-border rounded-xl shadow-lg hover:shadow-xl transition-all duration-300 backdrop-blur-sm;
  }

  .modern-card:hover {
    transform: translateY(-4px);
  }

  /* Button Styles */
  .btn-primary {
    @apply bg-primary hover:bg-blue-600 text-primary-foreground px-6 py-3 rounded-lg font-semibold transition-all duration-200 transform hover:scale-105 shadow-lg hover:shadow-xl;
  }

  .btn-secondary {
    @apply bg-transparent border-2 border-primary text-primary hover:bg-primary hover:text-white px-6 py-3 rounded-lg font-semibold transition-all duration-200;
  }

  /* Scroll Animations */
  .scroll-fade-up {
    opacity: 0;
    transform: translateY(30px);
    transition: opacity 0.6s ease-out, transform 0.6s ease-out;
  }

  .scroll-fade-up.animate {
    opacity: 1;
    transform: translateY(0);
  }

  .scroll-fade-left {
    opacity: 0;
    transform: translateX(-30px);
    transition: opacity 0.8s ease-out, transform 0.8s ease-out;
  }

  .scroll-fade-left.animate {
    opacity: 1;
    transform: translateX(0);
  }

  .scroll-fade-right {
    opacity: 0;
    transform: translateX(30px);
    transition: opacity 0.8s ease-out, transform 0.8s ease-out;
  }

  .scroll-fade-right.animate {
    opacity: 1;
    transform: translateX(0);
  }

  /* Stagger children animations */
  .stagger-children > * {
    opacity: 0;
    transform: translateY(20px);
    transition: opacity 0.6s ease-out, transform 0.6s ease-out;
  }

  .stagger-children.animate > * {
    opacity: 1;
    transform: translateY(0);
  }

  .stagger-children.animate > *:nth-child(1) { transition-delay: 0ms; }
  .stagger-children.animate > *:nth-child(2) { transition-delay: 100ms; }
  .stagger-children.animate > *:nth-child(3) { transition-delay: 200ms; }
  .stagger-children.animate > *:nth-child(4) { transition-delay: 300ms; }
  .stagger-children.animate > *:nth-child(5) { transition-delay: 400ms; }
  .stagger-children.animate > *:nth-child(6) { transition-delay: 500ms; }

  /* Keyframe Animations */
  @keyframes gradientShift {
    0%, 100% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
  }

  @keyframes fadeIn {
    from { opacity: 0; transform: translateY(20px); }
    to { opacity: 1; transform: translateY(0); }
  }

  @keyframes slideUp {
    from { opacity: 0; transform: translateY(40px); }
    to { opacity: 1; transform: translateY(0); }
  }

  @keyframes bounceGentle {
    0%, 100% { transform: translateY(0); }
    50% { transform: translateY(-10px); }
  }

  @keyframes pulse-slow {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.8; }
  }

  @keyframes spin-slow {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
  }

  /* Utility Classes */
  .animate-fade-in { animation: fadeIn 0.6s ease-in-out; }
  .animate-slide-up { animation: slideUp 0.8s ease-out; }
  .animate-bounce-gentle { animation: bounceGentle 2s ease-in-out infinite; }
  .animate-pulse-slow { animation: pulse-slow 3s ease-in-out infinite; }
  .animate-spin-slow { animation: spin-slow 8s linear infinite; }

  /* Focus States */
  .focus-ring {
    @apply focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2 focus:ring-offset-background;
  }

  /* Custom Scrollbar */
  .custom-scrollbar::-webkit-scrollbar {
    width: 8px;
  }

  .custom-scrollbar::-webkit-scrollbar-track {
    @apply bg-muted;
  }

  .custom-scrollbar::-webkit-scrollbar-thumb {
    @apply bg-border rounded-md;
  }

  .custom-scrollbar::-webkit-scrollbar-thumb:hover {
    @apply bg-primary;
  }

  /* Typography */
  .text-balance {
    text-wrap: balance;
  }

  /* Responsive utilities */
  .container-fluid {
    @apply w-full px-4 sm:px-6 lg:px-8;
  }
}

